import React, { Component } from 'react'
import foods from '../foods.json'
import foodsCategory from '../foods copy.json'
import Foodbox from './Foodbox';
import FoodCategory from './FoodCategory';
// import Addfood from './Addfood'
import Searchbar from './Searchbar';
import './../App.css';



export default class Foodlist extends Component {
    state = {
        foods: foods,
        showAdd: false,
        inputValue: '',
        filterFoods: foods,
        quantity: 0,
        totalCalories:0,
        listOfFoods: [],
        foodsCategory:foodsCategory
    }

    showAdd = () => {
        this.setState({ showAdd: !this.state.showAdd });
    };

    addOneFood = (newFoodObj) => {
        const foodCopy = [...this.state.foods];
        foodCopy.unshift(newFoodObj)

        this.setState({ foods: foodCopy })
    }




    render() {
        return (
            <div className="centered">

                <h1 className="foodlisth1">La Rovira</h1>

                <Searchbar filterOnChange={this.filterOnChange}
                    inputValue={this.state.inputValue} />
                <div className="list-add">
                <ul className="list-food">

                {
                    this.state.foodsCategory.map((category,index)=>{
                        return(
                            <FoodCategory
                            key={index}
                            {...category}    
                             />
                        )
                    })
                }
                    {/* {
                        this.state.filterFoods.map((oneFood, index) => {
                            return (
                                <Foodbox
                                key={index}
                                {...oneFood}
                                quantityChange={this.quantityChange}
                                quantity={this.state.quantity}
                                sumTheTotal={this.sumTheTotal} />
                            );
                        })
                    } */}
                </ul>

                </div>
            </div>
        )
    }
}
