import React, { Component } from 'react'
import foods from '../foods.json'
import foodsCategory from '../foods copy.json'
import Foodbox from './Foodbox';
import FoodCategory from './FoodCategory';
import Searchbar from './Searchbar';
import './../App.css';



export default class Foodlist extends Component {
    state = {
        foods: foods,
        showAdd: false,
        inputValue: '',
        filterFoods: foods,
        quantity: 0,
        totalCalories:0,
        listOfFoods: [],
        foodsCategory:foodsCategory
    }

    filterOnChange = e => {

        this.setState({
            inputValue: e.target.value
        })
        let searchValue = e.target.value.toLowerCase()
        let filterFoods = [...this.state.foods]

        let filteredFoods = filterFoods.filter(food => food.name.toLowerCase().includes(searchValue))


        this.setState({
            filterFoods: filteredFoods
        })
    }


    render() {
        return (
            <div className="centered">


                <Searchbar filterOnChange={this.filterOnChange}
                    inputValue={this.state.inputValue} />
                <div className="list-add">
                <ul className="list-food">       
                    {/* {
                        this.state.filterFoods.map((oneFood, index) => {
                            return (
                                <Foodbox
                                key={index}
                                {...oneFood}
                                quantityChange={this.quantityChange}
                                quantity={this.state.quantity}
                                sumTheTotal={this.sumTheTotal} />
                            );
                        })
                    } */}
                    <h1>{this.props.matchparams}</h1>
                </ul>

                </div>
            </div>
        )
    }
}
