import React, { useState } from 'react';
import './App.css';
import 'bulma/css/bulma.css';
import CategoryList from './Components/CategoryList';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import Foodlist from './Components/Foodlist';
import SearchComponent from './Components/SearchComponent';
import foods from './foods.json';
import Helmet from 'react-helmet';

function App() {
  const [buscar, setBuscar] = useState(false);

  return (
    <div className="App">
      <Helmet>
        <style>{`body { background-color: ${foods.backgroundColor}; min-height:100vh; font-family: "Comic Sans MS", cursive, sans-serif }`}</style>
      </Helmet>
      <img src="" alt=""/>
      <input type="button" onClick={() => setBuscar(!buscar)} value="buscar" />
      {buscar === true ? (
        <SearchComponent />
      ) : (
        <Router>
          <Route path="/" exact component={CategoryList} />
          <Route path="/category/:categoryName" exact component={Foodlist} />
        </Router>
      )}
    </div>
  );
}

export default App;
