import React, {
  Component
} from 'react';
import foods from '../foods.json';
import foodsCategory from '../foods copy.json';
import Foodbox from './Foodbox';
import Searchbar from './Searchbar';
import './../App.css';

export default class SearchComponent extends Component {
  state = {
    foods: foods,
    showAdd: false,
    inputValue: '',
    filterFoods: foods,
    listOfFoods: []
  };

  filterOnChange = (e) => {
    this.setState({
      inputValue: e.target.value,
    });
    let searchValue = e.target.value.toLowerCase();
    let filterFoods = [...this.state.foods];
    const arrayOfMenu = [];
    foods.map(x => arrayOfMenu.push(x))
    console.log(arrayOfMenu)
    let filteredFoods = filterFoods.filter((food) =>
      food.name.toLowerCase().includes(searchValue)
    );

    this.setState({
      filterFoods: filteredFoods,
    });
  };

  render() {

    return ( <
      div className = "centered" >
      <
      Searchbar filterOnChange = {
        this.filterOnChange
      }
      inputValue = {
        this.state.inputValue
      }
      /> <
      div className = "list-add" >
      <
      ul className = "list-food" >
      <
      /ul> <
      /div> <
      /div>
    );
  }
}
